INSERT INTO `wp_posts` VALUES("18", "1", "2012-10-04 14:30:10", "2012-10-04 22:00:10", "      <table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n   \r\n        <tr><td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n          The North Portland Tool Library seeks to build community. There are many great Portland area organizations that are assisting families and neighbors sustain and build livability in Portland. We encourage you to visit their sites and find out how these organizations are contributing to a better Portland.\r\n        </font></td></tr>\r\n        <tr><td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n          <ul>\r\n            <li><a href=\"http://www.portlandonline.com/index.cfm?c=26428\" target=\"_self\">The Bureau of Housing and Community Development</a></li>\r\n            <li><a href=\"http://www.metro-region.org/\" target=\"_self\">Metro</a></li>\r\n\r\n            <li><a href=\"http://www.portlandonline.com/northportland/index.cfm?c=35546\" target=\"_self\">North Portland Neighborhood Services</a></li>\r\n            <li><a href=\"http://www.portsmouthneighborhood.com\" target=\"_self\">Portsmouth Neighborhood Association</a></li>\r\n            <li><a href=\"http://www.peninsulacdc.org/\" target=\"_self\">Peninsula Community Development Corporation</a></li>\r\n            <li><a href=\"http://www.growing-gardens.org/\" target=\"_self\">Growing Gardens</a></li>\r\n            <li><a href=\"http://www.portlandonline.com/bes/index.cfm?&a=36329&c=31246\" target=\"_self\">Bureau of Environmental Services Downspout Disconnect Program</a></li>\r\n            <li><a href=\"http://www.tradeswomen.net/\" target=\"_self\">Oregon Tradeswomen</a></li>\r\n\r\n            <li>The Community Consulting Project Team of the Organizational Development Network, Oregon Chapter</li>\r\n          </ul>\r\n        </td></tr>\r\n\r\n      </table>", "Partners", "", "publish", "open", "closed", "", "