INSERT INTO `wp_posts` VALUES("20", "1", "2012-10-04 14:30:32", "2012-10-04 22:00:32", "<table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n<tbody>\r\n<tr>\r\n<td><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">\r\nThe North Portland Tool Library is an organization free to the community. We are able to provide this resource free of charge thanks to the thoughtful contributions of individuals and organizations. We would like to thank the following:\r\n</span></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<ul>\r\n	<li><a href=\"http://www.mmt.org/\" target=\"_self\">Meyer Memorial Trust</a></li>\r\n	<li><a href=\"http://www.kaiserpermanente.org/\" target=\"_self\">Kaiser Permanente</a></li>\r\n	<li><a href=\"http://www.rejuvenation.com/\" target=\"_self\">Rejuvenation</a></li>\r\n	<li><a href=\"http://www.portlandonline.com/index.cfm?c=26428\" target=\"_self\">The Bureau of Housing and Community Development</a> - Community Initiative Grants Program</li>\r\n	<li><a href=\"http://www.metro-region.org/\" target=\"_self\">Metro</a> - North Portland Community Enhancement Grant</li>\r\n	<li><a href=\"http://www.portlandonline.com/oni/\" target=\"_self\">North Portland Neighborhood Services</a></li>\r\n	<li>North Portland Trust Fund</li>\r\n	<li>North Portland Community Works</li>\r\n	<li><a href=\"http://www.pdxtex.com/\" target=\"_self\">Chris Glad</a> - for designing and developing our wonderful database system</li>\r\n	<li>Scott Jensen</li>\r\n	<li><a href=\"http://www.mcmenamins.com/\" target=\"_self\">McMenamins</a></li>\r\n	<li><a href=\"http://www.farrellrealty.com/\" target=\"_self\">Farrell Realty</a></li>\r\n	<li><a href=\"https://www.bankofthewest.com/BOW/home\" target=\"_self\">Bank of the West</a></li>\r\n	<li><a href=\"http://www.friendsofpir.com\" target=\"_self\">Friends of Portland International Raceway</a></li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">\r\nThe board and staff wish to thank all of you who have made recent donations of money and/or time. If you haven\'t donated lately please do so, your contributions are crucial to keeping the doors open, and remember they are tax deductible!</span></td>\r\n</tr>\r\n</tbody>\r\n</table>", "Sponsors", "", "publish", "open", "closed", "", "sponsors", "", "", "2012-10-15 07:16:27", "2012-10-15 14:46:27", "", "0", "http://www.northportlandtoollibrary.org/?page_id=20", "0", "page", "", "0");
