INSERT INTO `wp_posts` VALUES("26", "1", "2012-10-04 14:28:15", "2012-10-04 21:58:15", "<table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\"><!-- recognition.txt //-->\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\"><img src=\"http://www.northportlandtoollibrary.org//NPTL_Images/NPTL_Steering_Committee_1.jpg\" alt=\"\" width=\"550\" align=\"cente\" /></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"42\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The\r\nNorth Portland Tool Library was founded in October of 2004 by Matt\r\nMoritz, Laura Dalton, Jason Hatch, and Jason Henshaw.</span></td>\r\n</tr>\r\n<tr>\r\n<td valign=\"bottom\" width=\"74%\" height=\"205\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The\r\noperation and growth of the NPTL is guided by an <strong>all volunteer\r\n<span style=\"color: #ff0000;\">(non-paid)</span> board of directors</strong>\r\ncomprised of:</span></span>- Doug Hartman- Gerald Howard\r\n\r\n- Teri Thomas Petersen\r\n\r\n- Franny French\r\n\r\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The\r\nLibrary is staffed by:</span>\r\n\r\n- Arolia McSwain - Tool Coordinator\r\n\r\n&nbsp;</td>\r\n<td valign=\"bottom\" width=\"26%\"></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">\r\nThe primary objective of the Tool Library is to help build community\r\nand livability in this very diverse area of the city by providing\r\nthis free resource to all residents of North Portland <strong>free\r\nof charge</strong>. </span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"31\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The\r\nLibrary operates on a combination of grant funding and private donations.</span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"90\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><strong><span style=\"color: #ff0000;\">We\r\nare always seeking additional financial support</span></strong> from\r\nany sources available including private foundations, business and\r\nindividuals to help secure our future and to expand our hours of operation\r\nand our tool inventory. We gladly accept financial contributions and\r\nmay accept some tool donations upon inspection (if writing check-please\r\nwrite to North Portland Community Works - a 501C3 non-profit).</span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" align=\"left\" valign=\"top\" height=\"26\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The\r\nNorth Portland Tool Library is a project of the North Portland Community\r\nWorks a <strong>501C3, Tax ID: 93-1156762 </strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>", "About Us", "", "inherit", "open", "open", "", "6-revision-2", "", "", "2012-10-04 14:28:15", "2012-10-04 21:58:15", "", "6", "http://www.northportlandtoollibrary.org/%categoryname%/6-revision-2", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("40", "1", "2012-10-15 07:07:22", "2012-10-15 14:37:22", "<table width=\"761\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\"><!-- recognition.txt //-->\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\"><img src=\"http://www.northportlandtoollibrary.org//NPTL_Images/NPTL_Steering_Committee_1.jpg\" alt=\"\" width=\"550\" align=\"cente\" /></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"42\">\r\n<p style=\"text-align: left;\">The North Portland Tool Library was founded in October of 2004 by Matt Moritz,\r\nLaura Dalton, Jason Hatch, and Jason Henshaw.</p>\r\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The operation and growth of the NPTL is guided by an <strong>all volunteer <span style=\"color: #ff0000;\">(non-paid)</span> board of directors </strong>comprised of:</span></span>-\r\n\r\nDoug Hartman\r\n\r\nGerald Howard\r\n\r\nTeri Thomas Petersen\r\n\r\nPreston Peterson\r\n\r\nFranny French\r\n\r\nNicole Fink\r\n\r\nArolia McSwain\r\n\r\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The Library is staffed by:</span>\r\n\r\nLaurie Menet - Tool Coordinator\r\n\r\n&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The primary objective of the Tool Library is to help build community\r\nand livability in this very diverse area of the city by providing\r\nthis free resource to all residents of North Portland. </span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"31\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The Library operates on a combination of grant funding and private donations.</span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"90\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><strong><span style=\"color: #ff0000;\">We are always seeking additional financial support</span></strong> from\r\nany sources available including private foundations, business and\r\nindividuals to help secure our future and to expand our hours of operation\r\nand our tool inventory. We gladly accept financial contributions and\r\nmay accept some tool donations upon inspection (if writing check-please\r\nwrite to North Portland Community Works - a 501C3 non-profit).</span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" align=\"left\" valign=\"top\" height=\"26\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The North Portland Tool Library is a project of the North Portland Community\r\nWorks a <strong>501C3, Tax ID: 93-1156762 </strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>", "About Us", "", "inherit", "open", "open", "", "6-revision-4", "", "", "2012-10-15 07:07:22", "2012-10-15 14:37:22", "", "6", "http://northportlandtoollibrary.org/uncategorized/6-revision-4/", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("27", "1", "2012-10-04 14:28:33", "2012-10-04 21:58:33", "<iframe style=\"border: 1px solid #777777;\" src=\"http://www.google.com/calendar/embed?showNav=0&amp;mode=WEEK&amp;height=450&amp;wkst=2&amp;bgcolor=%23c0c0c0&amp;src=bogrpk9kiv8dnkm1h2pehqs01s%40group.calendar.google.com&amp;color=%238D6F47&amp;ctz=America%2FLos_Angeles\" frameborder=\"0\" scrolling=\"no\" width=\"550\" height=\"570\"></iframe> <br>\r\n          <font size=\"1\"  face=\"Verdana, Arial, Helvetica, sans-serif\">on-line calendar</font><br>\r\n          <br>", "Calendar", "", "inherit", "open", "open", "", "8-revision-2", "", "", "2012-10-04 14:28:33", "2012-10-04 21:58:33", "", "8", "http://www.northportlandtoollibrary.org/%categoryname%/8-revision-2", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("28", "1", "2012-10-04 14:28:59", "2012-10-04 21:58:59", "<table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n<tbody>\r\n<tr>\r\n<td>Sorry folks... we have had to disable the web forum. With our lack of appropriate manpower we just don\'t have anyone with the time needed to properly administer the forum and prevent the spammers and non-library posts from overwhelming the forum.</td>\r\n</tr>\r\n<tr>\r\n<td>Please become a \"fan\" of the North Portland Tool Library on Facebook. It\'s an easy way for you to stay connected with the most up to date information from us.\r\n\r\n<a href=\"http://www.facebook.com/pages/North-Portland-Tool-Library/129473544092\" target=\"_self\">http://www.facebook.com/pages/North-Portland-Tool-Library/129473544092</a></td>\r\n</tr>\r\n<tr>\r\n<td>If you have expertise in this area and are willing to manage a forum or other social networking page on behalf of the NPTL please contact us.</td>\r\n</tr>\r\n<tr>\r\n<td><form id=\"cse-search-box\" action=\"http://www.google.com/cse\">\r\n<div><input type=\"hidden\" name=\"cx\" value=\"017102997224722152591:gtbjwwmddpq\" /> <input type=\"hidden\" name=\"ie\" value=\"UTF-8\" /> <input type=\"text\" name=\"q\" size=\"31\" /> <input type=\"submit\" name=\"sa\" value=\"Search\" /></div>\r\n</form><strong>useful links</strong></td>\r\n<td><a href=\"http://www.refdesk.com/doitself.html\">Refdesk.com</a> an index of do-it-yourself sites and resources</td>\r\n<td><a href=\"http://www.remodelingmyspace.com/\">RemodelingMySpace.com</a> an online home improvement and remodeling resource</td>\r\n<td><a href=\"http://www.diynetwork.com/\">DIY Network</a> do-it-yourself projects</td>\r\n<td>City of Portland <a href=\"http://www.portlandonline.com/bds/\">Development Services</a>\r\n\r\n&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>", "Forum", "", "inherit", "open", "open", "", "10-revision-2", "", "", "2012-10-04 14:28:59", "2012-10-04 21:58:59", "", "10", "http://www.northportlandtoollibrary.org/%categoryname%/10-revision-2", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("29", "1", "2012-10-04 14:29:37", "2012-10-04 21:59:37", "<table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n<tbody>\r\n<tr>\r\n<td>Volunteers play an essential role at the NPTL, and are always welcome and appreciated. We can always use your help, whether you want to help man the Library during open hours, help with a special project, or just work to maintain some tools. We can always think of some way for you to help us out. If you have any time to give please contact us at 503-823-0209.</td>\r\n</tr>\r\n<tr>\r\n<td>We are seeking additional financial support from public sources, private foundations, business and individuals to help expand the number of tools and services that we can provide. We accept financial contributions (if writing check-please write to North Portland Community Works-a 501C3 non-profit) and may accept some tool donations upon inspection.</td>\r\n</tr>\r\n<tr>\r\n<td>We also welcome <a href=\"http://northportlandtoollibrary.org/partners\">partnerships</a>with organizations and skilled trades people, particularly to volunteer to staff or sponsor our free skill building workshops.In addition, we welcome anyone who would like to volunteer to help with general operations or outreach to please give us a call. We appreciate any help you are willing to give!The Tool Library is building <a href=\"http://northportlandtoollibrary.org/partners\">partnerships</a> with organizations and agencies, including: Peninsula Community Development Corporation (affordable housing), Growing Gardens (low-income household garden installations) and the Bureau of Environmental Services Downspout Disconnect program (clean rivers).</td>\r\n</tr>\r\n</tbody>\r\n</table>", "Get Involved", "", "inherit", "open", "open", "", "12-revision-3", "", "", "2012-10-04 14:29:37", "2012-10-04 21:59:37", "", "12", "http://www.northportlandtoollibrary.org/%categoryname%/12-revision-3", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("30", "1", "2012-10-04 14:29:52", "2012-10-04 21:59:52", "<table width=\"100%\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>Membership with the North Portland Tool Library is free and entitles you to borrow from our inventory of over 500 tools to assist in your project. There are a few simple steps, you must:</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"59%\">1) Be at Least 18 years of age\r\n2) A resident of North Portland\r\n3) Review the NPTL Borrowing Rules\r\n4) Fill out and sign the Borrowers Agreement&nbsp;\r\n\r\nTo register, simply come on down to the North Portland Tool Library when we are open to sign up. Documents needed to register:\r\n\r\n&nbsp;\r\n\r\n1) Oregon ID\r\n2) Utility Bill\r\n3) 2nd piece of mail to verify address.</td>\r\n<td><img src=\"http://www.northportlandtoollibrary.org/NPTL_Images/Member_600_Pam_Peterson.jpg\" alt=\"Member 600\" width=\"220\" height=\"165\" />\r\nPam Peterson &amp; crew bask in the glow of the free T-shirt she won for being our 600th registered member, we are now over 2000 members strong and growing!</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>The documents needed to register may be printed from our website and are available in either English or Spanish. To review a list of tools available to borrow, please check our current inventory on the <a href=\"tools\">tools</a> tab. The documents listed below are downloadable in PDF format:</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td><strong>Borrowing Rules </strong>- The rules for borrowing tools.<a href=\"http://www.northportlandtoollibrary.org/PDFs/NPTL%20Borrowing%20Rules_4_07.pdf\">\r\nEnglish</a><a href=\"http://www.northportlandtoollibrary.org/PDFs/NPTL%20Borrowing%20Rules%20Spanish_3_30_05.pdf\">\r\nSpanish</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td><strong>Borrowers Agreement</strong>- This is the form you need to fill out to become a member and borrow tools.<a href=\"http://www.northportlandtoollibrary.org/PDFs/NPTL_Borrowers_Agreement_Spanish_7_07.pdf\">\r\nEnglish\r\nSpanish\r\n</a></td>\r\n</tr>\r\n</tbody>\r\n</table>", "Membership", "", "inherit", "open", "open", "", "16-revision-2", "", "", "2012-10-04 14:29:52", "2012-10-04 21:59:52", "", "16", "http://www.northportlandtoollibrary.org/%categoryname%/16-revision-2", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("31", "1", "2012-10-04 14:30:10", "2012-10-04 22:00:10", "      <table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n   \r\n        <tr><td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n          The North Portland Tool Library seeks to build community. There are many great Portland area organizations that are assisting families and neighbors sustain and build livability in Portland. We encourage you to visit their sites and find out how these organizations are contributing to a better Portland.\r\n        </font></td></tr>\r\n        <tr><td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n          <ul>\r\n            <li><a href=\"http://www.portlandonline.com/index.cfm?c=26428\" target=\"_self\">The Bureau of Housing and Community Development</a></li>\r\n            <li><a href=\"http://www.metro-region.org/\" target=\"_self\">Metro</a></li>\r\n\r\n            <li><a href=\"http://www.portlandonline.com/northportland/index.cfm?c=35546\" target=\"_self\">North Portland Neighborhood Services</a></li>\r\n            <li><a href=\"http://www.portsmouthneighborhood.com\" target=\"_self\">Portsmouth Neighborhood Association</a></li>\r\n            <li><a href=\"http://www.peninsulacdc.org/\" target=\"_self\">Peninsula Community Development Corporation</a></li>\r\n            <li><a href=\"http://www.growing-gardens.org/\" target=\"_self\">Growing Gardens</a></li>\r\n            <li><a href=\"http://www.portlandonline.com/bes/index.cfm?&a=36329&c=31246\" target=\"_self\">Bureau of Environmental Services Downspout Disconnect Program</a></li>\r\n            <li><a href=\"http://www.tradeswomen.net/\" target=\"_self\">Oregon Tradeswomen</a></li>\r\n\r\n            <li>The Community Consulting Project Team of the Organizational Development Network, Oregon Chapter</li>\r\n          </ul>\r\n        </td></tr>\r\n\r\n      </table>", "Partners", "", "inherit", "open", "open", "", "18-revision-2", "", "", "2012-10-04 14:30:10", "2012-10-04 22:00:10", "", "18", "http://www.northportlandtoollibrary.org/%categoryname%/18-revision-2", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("32", "1", "2012-10-04 14:30:32", "2012-10-04 22:00:32", "      <table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n   \r\n        <tr><td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n          The North Portland Tool Library is an organization free to the community. We are able to provide this resource free of charge thanks to the thoughtful contributions of individuals and organizations. We would like to thank the following:\r\n        </font></td>\r\n        </tr>\r\n        <tr><td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n          <ul>\r\n            <li><a href=\"http://www.mmt.org/\" target=\"_self\">Meyer Memorial Trust</a></li>\r\n            <li><a href=\"http://www.kaiserpermanente.org/\" target=\"_self\">Kaiser Permanente</a></li>\r\n\r\n            <li><a href=\"http://www.rejuvenation.com/\" target=\"_self\">Rejuvenation</a></li>\r\n            <li><a href=\"http://www.portlandonline.com/index.cfm?c=26428\" target=\"_self\">The Bureau of Housing and Community Development</a> - Community Initiative Grants Program</li>\r\n            <li><a href=\"http://www.metro-region.org/\" target=\"_self\">Metro</a> - North Portland Community Enhancement Grant</li>\r\n            <li><a href=\"http://www.portlandonline.com/oni/\" target=\"_self\">North Portland Neighborhood Services</a></li>\r\n            <li>North Portland Trust Fund</li>\r\n\r\n            <li>North Portland Community Works</li>\r\n            <li><a href=\"http://www.pdxtex.com/\" target=\"_self\">Chris Glad</a> - for designing and developing our wonderful database system</li>\r\n            <li>Scott Jensen</li>\r\n            <li><a href=\"http://www.mcmenamins.com/\" target=\"_self\">McMenamins</a></li>\r\n            <li><a href=\"http://www.farrellrealty.com/\" target=\"_self\">Farrell Realty</a></li>\r\n\r\n            <li><a href=\"https://www.bankofthewest.com/BOW/home\" target=\"_self\">Bank of the West</a></li>\r\n            <li><a href=\"http://www.friendsofpir.com\" target=\"_self\">Friends of Portland International Raceway</a></li>\r\n          </ul>\r\n        </font></td></tr>\r\n        <tr><td><p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n        The board and staff wish to thank all of you who have made recent donations of money and/or time. If you haven\'t donated lately please do so, your contributions are crucial to keeping the doors open, and remember they are tax deductible!</font></p>\r\n            <a href=\"http://www.northportlandtoollibrary.org/PDFs/NPTLBirthdayPartyPoster_2011.pdf\"><img src=\"/img/announcements/NPTLBirthdayPartyPoster_2011_medPrev.jpg\" width=\"550\" height=\"713\" border=\"0\"></img></a></td></tr>\r\n\r\n      </table>", "Sponsors", "", "inherit", "open", "open", "", "20-revision-2", "", "", "2012-10-04 14:30:32", "2012-10-04 22:00:32", "", "20", "http://www.northportlandtoollibrary.org/%categoryname%/20-revision-2", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("33", "1", "2012-10-04 14:30:49", "2012-10-04 22:00:49", "<table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"3\">We currently have over 500 tools in stock including garden, carpentry, plumbing, carpentry and electrical tools, both power and hand tools. The tool list below is representative of the kinds of tools we carry but our stock is constantly changing due to maintenance, repair and donations.</td></tr>\r\n\r\n<tr>\r\n<td colspan=\"3\">There is also a downloadable <strong><a href=\"http://www.northortlandtoollibrary.org/PDFs/NPTL_Tool_List_8_06.pdf\">Tool List</a></strong> in PDF format, so that you can print a copy to help plan your project. <strong>Please note: </strong>this tool list is not comprehensive and all these tools may not be always available.</td></tr>\r\n\r\n<tr>\r\n<td colspan=\"3\">In many cases we have a more than one of each tool on the list in stock. The best way to get familiar with the holdings is to stop by during regular library hours and take a look around.</td></tr>\r\n\r\n<tr>\r\n<td colspan=\"3\">You are also welcome to give us a call to see if the particular tool or tools that you need are available. However, we do operate on a first come, first served basis, and often Saturday mornings can be very busy.</td></tr>\r\n\r\n<tr>\r\n<td colspan=\"3\">At this time we do not place items on hold.</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"3\">Tool list</td>\r\n</tr>\r\n<tr>\r\n<td align=\"left\" width=\"33%\"><strong>Carpentry\r\n&amp; Woodworking</strong>\r\n<ul>\r\n	<li>Block Plane</li>\r\n	<li>Cat’s Paw</li>\r\n	<li>Chisels: Wood</li>\r\n	<li>Clamp: Right-Angle</li>\r\n	<li>Clamps: Bar</li>\r\n	<li>Clamps: C</li>\r\n	<li>Clamps: Quick 6\"</li>\r\n	<li>Coping Saw</li>\r\n	<li>Crow-Bar</li>\r\n	<li>Hammer: Finish</li>\r\n	<li>Hammer: Framing</li>\r\n	<li>Impact Driver Set w/bits</li>\r\n	<li>Level:Laser 2\"</li>\r\n	<li>Level:Laser 2\"</li>\r\n	<li>Level:Torpedo</li>\r\n	<li>Nail Sets</li>\r\n	<li>Nailer: Flooring</li>\r\n	<li>Plumb Bob</li>\r\n	<li>Pocket Screw Jig</li>\r\n	<li>Protractor/Angle Finder</li>\r\n	<li>Pry Bars</li>\r\n	<li>Router</li>\r\n	<li>Router Table</li>\r\n	<li>Saw: Door Jamb</li>\r\n	<li>Saw: Japanese Pull</li>\r\n	<li>Saw: Rip 24”</li>\r\n	<li>Scribe/Compass</li>\r\n	<li>Square: Framing</li>\r\n	<li>Square: Speed</li>\r\n	<li>Square: Tri</li>\r\n	<li>Stud Finder</li>\r\n	<li>Tape: 100’ Reel</li>\r\n	<li>Tape: Measure 25’</li>\r\n	<li>Trim Gauge</li>\r\n	<li>Utility Knife</li>\r\n	<li>Vernier Caliper</li>\r\n</ul>\r\n</td>\r\n<td align=\"center\" valign=\"top\" width=\"33%\">\r\n<div align=\"left\">\r\n\r\n<strong>Garden Tools</strong><strong><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif;\">\r\n</span></strong>\r\n<ul>\r\n	<li>Axe</li>\r\n	<li>Bar: Cutting/scraping</li>\r\n	<li>Bar: Digging 72\"</li>\r\n	<li>Drain Spade</li>\r\n	<li>Edger: Electric</li>\r\n	<li>Hatchet</li>\r\n	<li>Hoe: Garden</li>\r\n	<li>Lawn Aerator</li>\r\n	<li>Lawn Mower: Electric</li>\r\n	<li>Pick: Digging</li>\r\n	<li>Pick: Mattock</li>\r\n	<li>Post Setter</li>\r\n	<li>Pruner: Lopping</li>\r\n	<li>Pruners: Hand</li>\r\n	<li>Rake: Grading</li>\r\n	<li>Rake: Leaf</li>\r\n	<li>Saw: Bow 21\"</li>\r\n	<li>Saw: Branch</li>\r\n	<li>Seed Spreader</li>\r\n	<li>Shovel: Flat Point</li>\r\n	<li>Shovel: Round Point</li>\r\n	<li>Shovel: Small Garden</li>\r\n	<li>Shovel: Snow</li>\r\n	<li>Tamper: 8” x 8”</li>\r\n	<li>Tiller: Electric</li>\r\n	<li>Trimmer: Electric</li>\r\n	<li>Trimmer: Hedge (Manual).</li>\r\n	<li>Trimmer: Pole</li>\r\n	<li>Trowel: Garden</li>\r\n	<li>Turning Fork</li>\r\n	<li>Weed Puller</li>\r\n</ul>\r\n</div></td>\r\n<td valign=\"top\" width=\"33%\">\r\n<div align=\"left\">\r\n\r\n<strong>Power Tools</strong>\r\n<ul>\r\n	<li>Angle Grinder</li>\r\n	<li>Cement Siding Shears</li>\r\n	<li>Compressor: Air</li>\r\n	<li>Demolition Hammer</li>\r\n	<li>Dremel Rotary Tool</li>\r\n	<li>Drill: ½” Hammer</li>\r\n	<li>Drill: ½” VSR</li>\r\n	<li>Drill: 3/8” VSR</li>\r\n	<li>Drill: Right Angle</li>\r\n	<li>Nailer: Brad (18 ga.)</li>\r\n	<li>Nailer: Finish (16 ga.)</li>\r\n	<li>Nailer: Framing</li>\r\n	<li>Nailer: Roofing Coil</li>\r\n	<li>Power Planer</li>\r\n	<li>Rotozip Cutting Tool</li>\r\n	<li>Sander: 18” Belt</li>\r\n	<li>Sander: 21” Belt</li>\r\n	<li>Sander: Detail</li>\r\n	<li>Sander: Orbital Palm</li>\r\n	<li>Saw: 10” Miter</li>\r\n	<li>Saw: 10” Table (Portable)</li>\r\n	<li>Saw: 14” Metal Cutoff</li>\r\n	<li>Saw: Circular</li>\r\n	<li>Saw: Jigsaw</li>\r\n	<li>Saw: Reciprocating</li>\r\n	<li>Saw: Tile</li>\r\n	<li>Stand: Mitre Saw</li>\r\n	<li>Stapler: Narrow Crown</li>\r\n	<li>Vac: 8 gal Wet/Dry</li>\r\n</ul>\r\n</div></td>\r\n</tr>\r\n<tr valign=\"top\">\r\n<td>\r\n<div align=\"left\"><strong>Electrical\r\n&amp; Mechanical </strong>\r\n<ul>\r\n	<li>Conduit Bender</li>\r\n	<li>Digital Multimeter</li>\r\n	<li>Hacksaws</li>\r\n	<li>Hex Key Sets</li>\r\n	<li>Nipper: 10”</li>\r\n	<li>Pliers: Fencing/Box</li>\r\n	<li>Pliers: Linesman</li>\r\n	<li>Pliers: Slotted</li>\r\n	<li>Pliers: Vice Grip</li>\r\n	<li>Screwdrivers</li>\r\n	<li>Snips: 12\" Sheet</li>\r\n	<li>Snips: Tin</li>\r\n	<li>Socket Sets</li>\r\n	<li>Tape: 100’ Fish</li>\r\n	<li>Telephone Crimping Tool</li>\r\n	<li>Tester: Circuit</li>\r\n	<li>Tester: GFI</li>\r\n	<li>Tester: Receptacle</li>\r\n	<li>Wrench: Crescent</li>\r\n	<li>Wrench: Pipe</li>\r\n	<li>Wrench: Slip</li>\r\n</ul>\r\n</div></td>\r\n<td>\r\n<div align=\"left\">\r\n\r\n<strong>Drywall &amp; Masonry</strong>\r\n<ul>\r\n	<li>Caulking Guns</li>\r\n	<li>Knife: Corner</li>\r\n	<li>Knife: Mud 4”</li>\r\n	<li>Knife: Taping 12”</li>\r\n	<li>Knife: Taping 8” Panel</li>\r\n	<li>Hoist</li>\r\n	<li>Sander:Hand</li>\r\n	<li>Saw: Drywall</li>\r\n	<li>Scraper 3\"</li>\r\n	<li>Scraper 4\"</li>\r\n	<li>Scraper: Floor</li>\r\n	<li>T-Square 48”</li>\r\n</ul>\r\n</div></td>\r\n<td>\r\n<div align=\"left\">\r\n\r\n<strong>Other Tools</strong>\r\n<ul>\r\n	<li>Drill Bits</li>\r\n	<li>Dead-Blow Hammer</li>\r\n	<li>Extension Cords</li>\r\n	<li>Flashlight, adj</li>\r\n	<li>Hand Metal Detector</li>\r\n	<li>Hand Truck</li>\r\n	<li>Heat Gun</li>\r\n	<li>Hole Saw Kit</li>\r\n	<li>Ladder: 17’ Multi</li>\r\n	<li>Ladder: 21\' Multi</li>\r\n	<li>Ladder: 6’ Fiberglass</li>\r\n	<li>Masonry Hammer Bits</li>\r\n	<li>Roofing Spade Sledge</li>\r\n	<li>Hammer 10lb Sledge</li>\r\n	<li>Hammer: 3lb</li>\r\n	<li>Spade Bit Set</li>\r\n	<li>Tile Cutter 14”</li>\r\n	<li>Tile Saw: 7” Wet</li>\r\n	<li>Wallpaper Steamer</li>\r\n	<li>Work Lights</li>\r\n</ul>\r\n</div></td>\r\n</tr>\r\n<tr valign=\"top\">\r\n<td>\r\n<div align=\"left\"><strong>Carpet\r\nTools </strong>\r\n<ul>\r\n	<li>Carpet Cutter</li>\r\n	<li>Knee Kicker</li>\r\n	<li>Power Stretcher</li>\r\n	<li>Seam Iron</li>\r\n	<li>Seam Roller</li>\r\n	<li>Stair Tool</li>\r\n</ul>\r\n</div></td>\r\n<td>\r\n<div align=\"left\">\r\n\r\n<strong>Cement\r\nTools</strong>\r\n<ul>\r\n	<li>Floats Seamer</li>\r\n	<li>Trowel: Notched</li>\r\n	<li>Trowel: V</li>\r\n	<li>Trowels: Finish</li>\r\n</ul>\r\n</div></td>\r\n<td>\r\n<div align=\"left\"><strong>Bike\r\nTools </strong>\r\n<ul>\r\n	<li>Full Set Bike Tools</li>\r\n	<li>Mechanic Stand</li>\r\n</ul>\r\n</div></td>\r\n</tr>\r\n</tbody>\r\n</table>", "Tools", "", "inherit", "open", "open", "", "22-revision-2", "", "", "2012-10-04 14:30:49", "2012-10-04 22:00:49", "", "22", "http://www.northportlandtoollibrary.org/%categoryname%/22-revision-2", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("34", "1", "2012-10-04 14:31:03", "2012-10-04 22:01:03", "<table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n<tbody>\r\n<tr>\r\n<td>Never used a power tool before? Tired of listening to that leaky faucet? Not only does the North Portland Tool Library have tools, we also sponsor hands-on workshops to help you learn to use those tools more effectively. The workshops are free and will be designed and run by skilled individuals from the community or partner organizations.</td>\r\n</tr>\r\n<tr>\r\n<td height=\"248\"><!-- Upcomming Workshops //-->\r\n<strong><span style=\"color: #aa0066;\">Upcoming Workshops:</span></strong>\r\n\r\nPlease consider becoming a \"fan\" of the North Portland Tool Library on Facebook. We have found it easiest to communicate our upcoming workshops there. We\'ll also try to post them up here and on the calendar, but we found Facebook to be the most effective for getting the word out. It\'s also an easy way for you to stay connected with the most up to date information from us.\r\n\r\n<a href=\"http://www.facebook.com/pages/North-Portland-Tool-Library/129473544092\" target=\"_self\">http://www.facebook.com/pages/North-Portland-Tool-Library/129473544092</a></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\" align=\"left\"><strong>Pictures from our very first \"Basic Skills\" workshop</strong></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"http://www.northportlandtoollibrary.org/NPTL_Images/WS_1_29_05_1.jpg\" alt=\"\" width=\"250\" height=\"166\" /></td>\r\n<td><img src=\"http://www.northportlandtoollibrary.org/NPTL_Images/WS_1_29_05_2.jpg\" alt=\"\" width=\"250\" height=\"166\" /></td>\r\n</tr>\r\n<tr align=\"left\" valign=\"top\">\r\n<td><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: xx-small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: xx-small;\">Workshop instructor Laura Dalton blathers on about the slide miter saw and safe use.</span></span></td>\r\n<td><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: xx-small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: xx-small;\">North Portland resident Pamela Jeanne lets the chips fly under Laura\'s watchful eye.</span></span></td>\r\n</tr>\r\n<tr align=\"left\" valign=\"top\">\r\n<td><img src=\"http://www.northportlandtoollibrary.org/NPTL_Images/WS_1_29_05_3.jpg\" alt=\"\" width=\"250\" height=\"166\" /></td>\r\n<td><img src=\"http://www.northportlandtoollibrary.org/NPTL_Images/WS_1_29_05_4.jpg\" alt=\"\" width=\"250\" height=\"166\" /></td>\r\n</tr>\r\n<tr align=\"left\" valign=\"top\">\r\n<td><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: xx-small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: xx-small;\">North Portland resident Lorenzo Carmona practices sinking sheet rock screws to the perfect depth with the cordless drill.</span></span></td>\r\n<td><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: xx-small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: xx-small;\">Sisters and NP residents Zannah (left) and Jennifer Martell (center) experience the thrills of reciprocating saw demolition with the assistance of Oregon Tradeswomen instructor Dawn Jones (right).</span></span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\">Are you skilled in the building trades and interested in sharing your knowledge with others? Or maybe you have a great idea for a workshop. If so, we would love to hear from you. Our primary goal is to give residents of North Portland the information they need, so community input is crucial.\r\n\r\n\r\n\r\nContact us with\r\nyour input.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>", "Workshops", "", "inherit", "open", "open", "", "24-revision-2", "", "", "2012-10-04 14:31:03", "2012-10-04 22:01:03", "", "24", "http://www.northportlandtoollibrary.org/%categoryname%/24-revision-2", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("52", "1", "2013-07-29 19:27:25", "2013-07-30 02:27:25", "<img class=\"alignleft\" alt=\"\" src=\"http://www.northportlandtoollibrary.org/NPTL_Images/Firehouse.gif\" width=\"250\" height=\"188\" />\n\nThe North Portland Tool Library (NPTL) is a community resource dedicated to building community and fostering sustainability by providing residents with tools and the power to use them. We loan a wide variety of tools to community members <strong>free of charge</strong>. The NPTL benefits North Portland residents by reducing the costs of maintaining and improving the places in which we live, work, and play. The NTPL helps support diverse, livable neighborhoods and fosters community pride.\n\n<strong><span style=\"color: #ff0000;\">We are currently under construction.  As with all things old, sometimes we need a bit of maintenance to keep us going, and with that there comes a bit of clean up as well.    We are hopeful our new Web site will be more useful for our Members and we appreciate your understanding while we do the work.  We are open during our remodel!!! </span></strong>\n\n<address> </address><address> </address>\n<p style=\"text-align: center;\"><a title=\"Paypal\" href=\"https://www.paypal.com/cgi-bin/webscr?cmd=_s-xclick&amp;hosted_button_id=R7MM3FQWEFJ72\" target=\"_blank\"><input type=\"image\" alt=\"PayPal - The safer, easier way to pay online!\" name=\"submit\" src=\"https://www.paypalobjects.com/en_US/i/btn/btn_donateCC_LG.gif\" /></a>\n<img alt=\"\" src=\"https://www.paypalobjects.com/en_US/i/scr/pixel.gif\" width=\"1\" height=\"1\" border=\"0\" /></p>\n\n<table border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\n<tbody>\n<tr>\n<td colspan=\"2\">\n<div></div>\n<div>The North Portland Tool Library is a project of North Portland Community Works a qualified organization</div>\n<div>for tax exempt status under IRS Section 501(c)(3).  Tax ID: 93-1156762   Donations are tax deductible as the law allows.</div>\nRegistration with the Library is free to all residents of <strong>North Portland</strong> and there are no costs aside from late fees and replacement charges. Please see the <a href=\"http://northportlandtoollibrary.org/membership/\">membership</a> tab for additional details about registration and requirements.</td>\n<td colspan=\"2\" align=\"center\"></td>\n<td colspan=\"2\" align=\"center\" width=\"400\" height=\"85\">\n<table width=\"93%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\n<tbody>\n<tr>\n<td>Become a \"fan\" of the North Portland Tool Library on Facebook. It\'s an easy way for you to stay connected with the most up to date information from us!<a href=\"https://www.facebook.com/pages/North-Portland-Tool-Library/129473544092\"><img class=\"alignnone size-full wp-image-60\" title=\"facebook_logo-300x300\" alt=\"\" src=\"http://northportlandtoollibrary.org/wp-content/uploads/2012/10/facebook_logo-300x300-e1359829256672.jpg\" width=\"25\" height=\"25\" /></a></td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n</tbody>\n</table>", "Tools and the Power to use them", "", "inherit", "open", "open", "", "4-autosave", "", "", "2013-07-29 19:27:25", "2013-07-30 02:27:25", "", "4", "http://northportlandtoollibrary.org/uncategorized/4-autosave/", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("35", "1", "2012-10-04 14:25:45", "2012-10-04 21:55:45", "<table border=\"0\" cellspacing=\"5\" cellpadding=\"5\">\r\n<tbody>\r\n<tr>\r\n<td width=\"56%\">The North Portland Tool Library (NPTL) is a community resource dedicated to building community and fostering sustainability by providing residents with tools and the power to use them. We loan a wide variety of tools to community members <strong>free of charge</strong>. The NPTL benefits North Portland residents by reducing the costs of maintaining and improving the places in which we live, work, and play. The NTPL helps support diverse, livable neighborhoods and fosters community pride.</td>\r\n<td align=\"right\" valign=\"top\" width=\"44%\"><img src=\"http://www.northportlandtoollibrary.org/NPTL_Images/Firehouse.gif\" alt=\"\" width=\"250\" /></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\">Registration with the Library is free to all residents of <strong>North Portland</strong> and there are no costs aside from late fees and replacement charges. Please see the <a href=\"http://northportlandtoollibrary.com/membership/\">membership</a> tab for additional details about registration and requirements.</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" align=\"center\">\r\n\r\n[caption id=\"attachment_105\" align=\"alignnone\" width=\"490\"]<a href=\"http://www.northportlandtoollibrary.com/index-tool-library-home/attachment/nptlpausebirthdayposteroct2/\" rel=\"attachment wp-att-105\"><img class=\" wp-image-105 \" title=\"North Portland Birthday Party @ PAUSE\" src=\"http://www.northportlandtoollibrary.com/wp-content/uploads/2011/11/NPTLPauseBirthdayPosterOct2.jpg\" alt=\"\" width=\"490\" height=\"634\" /></a> Come out and join us for a fun filled night of auctions, raffles, and great food!<br />25% of all the food proceeds will be donated back to Your tool library!!<br />Some come on out and say hi to everyone!![/caption]</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" align=\"center\" width=\"400\" height=\"85\">\r\n<table width=\"93%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>Become a \"fan\" of the North Portland Tool Library on Facebook. It\'s an easy way for you to stay connected with the most up to date information from us!<a href=\"http://www.facebook.com/pages/North-Portland-Tool-Library/129473544092\" target=\"_self\">http://www.facebook.com/pages/North-Portland-Tool-Library/129473544092</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>", "Tools and the Power to use them.", "", "inherit", "open", "open", "", "4-revision-2", "", "", "2012-10-04 14:25:45", "2012-10-04 21:55:45", "", "4", "http://northportlandtoollibrary.org/uncategorized/4-revision-2/", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("38", "1", "2013-07-23 19:05:24", "2013-07-24 02:05:24", "<table width=\"761\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\"><!-- recognition.txt //-->\n<tbody>\n<tr>\n<td colspan=\"2\"><img alt=\"\" src=\"http://www.northportlandtoollibrary.org//NPTL_Images/NPTL_Steering_Committee_1.jpg\" width=\"550\" align=\"cente\" /></td>\n</tr>\n<tr>\n<td colspan=\"2\" height=\"42\">\n<p style=\"text-align: left;\">The North Portland Tool Library was founded in October of 2004 by Matt Moritz,\nLaura Dalton, Jason Hatch, and Jason Henshaw.</p>\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The operation and growth of the NPTL is guided by an <strong>all volunteer board of directors</strong></span></span>\n\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><strong> </strong>comprised of:</span></span>-\n\nDoug Hartman\n\nGerald Howard\n\nTeri Thomas Petersen\n\nPreston Browning\n\nFranny French\n\nArolia McSwain\n\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The Library is staffed by:</span>\n\nLaurie Menet - Tool Coordinator\n\n&nbsp;</td>\n</tr>\n<tr>\n<td colspan=\"2\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The primary objective of the Tool Library is to help build community\nand livability in this very diverse area of the city by providing\nthis free resource to all residents of North Portland. </span></td>\n</tr>\n<tr>\n<td colspan=\"2\" height=\"31\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The Library operates on a combination of grant funding and private donations.</span></td>\n</tr>\n<tr>\n<td colspan=\"2\" height=\"90\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><strong><span style=\"color: #ff0000;\">We are always seeking additional financial support</span></strong> from\nany sources available including private foundations, business and\nindividuals to help secure our future and to expand our hours of operation\nand our tool inventory. We gladly accept financial contributions and\nmay accept some tool donations upon inspection (if writing check-please\nwrite to North Portland Community Works - a 501C3 non-profit).</span><a title=\"Paypal\" href=\"https://www.paypal.com/cgi-bin/webscr?cmd=_s-xclick&amp;hosted_button_id=R7MM3FQWEFJ72\" target=\"_blank\"><input type=\"image\" alt=\"PayPal - The safer, easier way to pay online!\" name=\"submit\" src=\"https://www.paypalobjects.com/en_US/i/btn/btn_donateCC_LG.gif\" /></a></td>\n</tr>\n<tr>\n<td colspan=\"2\" align=\"left\" valign=\"top\" height=\"26\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The North Portland Tool Library is a project of the North Portland Community\nWorks a <strong>501C3, Tax ID: 93-1156762 </strong></span></td>\n</tr>\n</tbody>\n</table>", "About Us", "", "inherit", "open", "open", "", "6-autosave", "", "", "2013-07-23 19:05:24", "2013-07-24 02:05:24", "", "6", "http://northportlandtoollibrary.org/uncategorized/6-autosave/", "0", "revision", "", "0");
