INSERT INTO `wp_posts` VALUES("196", "1", "2013-06-25 12:30:23", "2013-06-25 20:00:23", "<table width=\"761\" border=\"0\" cellspacing=\"5\" cellpadding=\"5\"><!-- recognition.txt //-->\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\"><img src=\"http://www.northportlandtoollibrary.org//NPTL_Images/NPTL_Steering_Committee_1.jpg\" alt=\"\" width=\"550\" align=\"cente\" /></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"42\">\r\n<p style=\"text-align: left;\">The North Portland Tool Library was founded in October of 2004 by Matt Moritz,\r\nLaura Dalton, Jason Hatch, and Jason Henshaw.</p>\r\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The operation and growth of the NPTL is guided by an <strong>all volunteer board of directors</strong></span></span>\r\n\r\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><strong> </strong>comprised of:</span></span>-\r\n\r\n&nbsp;\r\n\r\nDoug Hartman\r\n\r\nGerald Howard\r\n\r\nTeri Thomas Petersen\r\n\r\nPreston Brown\r\n\r\nFranny French\r\n\r\nNicole Fink\r\n\r\nArolia McSwain\r\n\r\n&nbsp;\r\n\r\n<span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The Library is staffed by:</span>\r\n\r\nLaurie Menet - Tool Coordinator\r\n\r\n&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The primary objective of the Tool Library is to help build community\r\nand livability in this very diverse area of the city by providing\r\nthis free resource to all residents of North Portland. </span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"31\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The Library operates on a combination of grant funding and private donations.</span></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" height=\"90\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\"><strong><span style=\"color: #ff0000;\">We are always seeking additional financial support</span></strong> from\r\nany sources available including private foundations, business and\r\nindividuals to help secure our future and to expand our hours of operation\r\nand our tool inventory. We gladly accept financial contributions and\r\nmay accept some tool donations upon inspection (if writing check-please\r\nwrite to North Portland Community Works - a 501C3 non-profit).</span><a title=\"Paypal\" href=\"https://www.paypal.com/cgi-bin/webscr?cmd=_s-xclick&amp;hosted_button_id=R7MM3FQWEFJ72\" target=\"_blank\"><input type=\"image\" name=\"submit\" src=\"https://www.paypalobjects.com/en_US/i/btn/btn_donateCC_LG.gif\" alt=\"PayPal - The safer, easier way to pay online!\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" align=\"left\" valign=\"top\" height=\"26\"><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: small;\">The North Portland Tool Library is a project of the North Portland Community\r\nWorks a <strong>501C3, Tax ID: 93-1156762 </strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>", "About Us", "", "inherit", "open", "open", "", "6-revision-10", "", "", "2013-06-25 12:30:23", "2013-06-25 20:00:23", "", "6", "http://northportlandtoollibrary.org/uncategorized/6-revision-10/", "0", "revision", "", "0");
